
# require library: pip install requests -t ./package
# filename: lambda_function.py

import json
import requests
import os

def validateAppStoreReceiptInternal(payload, url, shared_secret):
    reqPayloadObj = {
        "receipt-data" : payload,
        "password" : shared_secret,
    }
    reqPayloadJson = json.dumps(reqPayloadObj)
    reqHeader = {"Content-Type": "application/json"}

    try:
        res = requests.post(url, data=reqPayloadJson, headers=reqHeader)
        return res.json().get('status')
    except Exception as e:
        errorMsg = e.reason
        return -1

def validateAppStoreReceipt(payload, shared_secret):
    urlSandbox = "https://sandbox.itunes.apple.com/verifyReceipt"
    urlProduction = "https://buy.itunes.apple.com/verifyReceipt"

    status = validateAppStoreReceiptInternal(payload, urlProduction, shared_secret)
    if status == 21007:
        status = validateAppStoreReceiptInternal(payload, urlSandbox, shared_secret)
    
    return status == 0
    
def lambda_handler(event, context):

    # value of parameter cannot contain special characters, and appstore payload contain those one
    # so need convert first:
    # =    ->    %3D
    # +    ->    %2B
    # &    ->    %26
    # %    ->    %25
    # ?    ->    %3F

    params = event.get('queryStringParameters')
    payload = params.get('payload')
    shared_secret = os.environ.get('shared_secret')

    isValid = validateAppStoreReceipt(payload, shared_secret)

    return {
        'statusCode': 200,
        'body' : isValid
    }

