import json
import requests
import os
import firebase_admin
from firebase_admin import credentials, firestore
from datetime import datetime, timezone

def logDebug(isDebug, msg):
    if isDebug:
        print(msg)

def logToDatabase(serviceAccountFile, verifyResult, verifyMsg, isDebug, transactionId, productId, price, currency, userId):
    try:
        cred = credentials.Certificate(serviceAccountFile)
        app = firebase_admin.initialize_app(cred)

        store = firestore.client()
        doc_ref = store.collection('iap_history')

        now = datetime.now(timezone.utc)

        props = {
            'timestamp': now,
            'platform': 'appstore',
            'verify_result': verifyResult,
            'verify_msg': verifyMsg,
            'transaction_id': transactionId,
            'product_id': productId,
            'price': price,
            'currency': currency,
            'user_id': userId,
        }
        doc_ref.add(props)
    except Exception as e:
        errorMsg = str(e)
        logDebug(isDebug, "Exception when logging to database: " + errorMsg)

def validateAppStoreReceiptInternal(payload, url, shared_secret, isDebug):
    reqPayloadObj = {
        "receipt-data" : payload,
        "password" : shared_secret,
    }
    reqPayloadJson = json.dumps(reqPayloadObj)
    reqHeader = {"Content-Type": "application/json"}

    try:
        res = requests.post(url, data=reqPayloadJson, headers=reqHeader)
        resJson = res.json()
        logDebug(isDebug, f"Response from App Store: {resJson}")
        return {
            'status': resJson.get('status'),
        }
    except Exception as e:
        errorMsg = str(e)
        logDebug(isDebug, f"Exception when validate app store receipt: {errorMsg}")
        return {
            'status': -1,
            'error_msg': errorMsg
        }

def statusCodeToString(status):
    statusMap = {
        0: "The receipt is valid.",
        21000: "The App Store could not read the JSON object you provided.",
        21002: "The data in the receipt-data property was malformed or missing.",
        21003: "The receipt could not be authenticated.",
        21004: "The shared secret you provided does not match the shared secret on file for your account.",
        21005: "The receipt server is not currently available.",
        21006: "This receipt is valid but the subscription has expired.",
        21007: "This receipt is from the test environment, but it was sent to the production environment for verification.",
        21008: "This receipt is from the production environment, but it was sent to the test environment for verification.",
    }
    return statusMap.get(status, "Unknown status code.")

def validateAppStoreReceipt(payload, shared_secret, isDebug, serviceAccountFile, transactionId, productId, price, currency, userId):
    urlSandbox = "https://sandbox.itunes.apple.com/verifyReceipt"
    urlProduction = "https://buy.itunes.apple.com/verifyReceipt"

    validateResult = validateAppStoreReceiptInternal(payload, urlProduction, shared_secret, isDebug)
    logDebug(isDebug, f"Status from production: {validateResult['status']}")
    if validateResult['status'] == 21007:
        validateResult = validateAppStoreReceiptInternal(payload, urlSandbox, shared_secret, isDebug)
        logDebug(isDebug, f"Status from sandbox: {validateResult['status']}")

    strStatus = statusCodeToString(validateResult['status'])
    logDebug(isDebug, strStatus)

    verifyResult = validateResult['status'] == 0
    if validateResult['status'] < 0:
        strStatus = validateResult['error_msg']
    logToDatabase(serviceAccountFile, verifyResult, strStatus, isDebug, transactionId, productId, price, currency, userId)
    
    return verifyResult
    
def lambda_handler(event, context):

    # value of parameter cannot contain special characters, and appstore payload contain those one
    # so need convert first:
    # =    ->    %3D
    # +    ->    %2B
    # &    ->    %26
    # %    ->    %25
    # ?    ->    %3F

    params = event.get('queryStringParameters')
    payload = params.get('payload')
    transactionId = params.get('transactionId')
    productId = params.get('productId')
    price = params.get('price')
    currency = params.get('currency')
    userId = params.get('userId')
    shared_secret = os.environ.get('shared_secret')
    serviceAccountFile = "service_account.json"

    isValid = validateAppStoreReceipt(payload, shared_secret, False, serviceAccountFile, transactionId, productId, price, currency, userId)

    return {
        'statusCode': 200,
        'body' : isValid
    }

